namespace SprykerSdkTest\Spryk\Integration\Zed\Business;

use Codeception\Test\Unit;
use SprykerSdkTest\Module\ClassName;

/**
 * Auto-generated group annotations
 *
 * @group SprykerSdkTest
 * @group Spryk
 * @group Integration
 * @group Zed
 * @group Business
 * @group AddZedBusinessFacadeTest
 * Add your own group annotations below this line
 */
class AddZedBusinessFacadeTest extends Unit
{
    /**
     * @var \SprykerSdkTest\SprykIntegrationTester
     */
    protected $tester;

    /**
     * @return void
     */
    public function testAddsZedBusinessFacade(): void
    {
        $this->tester->run($this, [
            '--module' => 'FooBar',
        ]);

        $this->tester->assertClassOrInterfaceExtends(ClassName::ZED_FACADE, ClassName::ZED_ABSTRACT_FACADE);
    }

    /**
     * @return void
     */
    public function testAddsZedBusinessFacadeOnProjectLayer(): void
    {
        $this->tester->run($this, [
            '--module' => 'FooBar',
            '--mode' => 'project',
        ]);

        $this->tester->assertClassOrInterfaceExtends(ClassName::PROJECT_ZED_FACADE, ClassName::ZED_ABSTRACT_FACADE);
    }

    /**
     * @return void
     */
    public function testAddsZedBusinessFacadeThatExtendsSameCoreBaseClass(): void
    {
        $this->tester->run($this, [
            '--module' => 'FooBar',
            '--mode' => 'core',
        ]);

        $this->tester->run($this, [
            '--module' => 'FooBar',
            '--mode' => 'project',
        ]);

        $this->tester->assertClassOrInterfaceExtends(ClassName::PROJECT_ZED_FACADE, ClassName::ZED_FACADE);
    }
}
